import discordimport datetimefrom typing import Unionimport randomimport inspectimport jsonimport discord.extfrom discord.ext import commandsfrom typing import Unionfrom datetime import datetimefrom discord import Embedimport asyncioclass Utility(commands.Cog):    def __init__(self, bot):    	self.bot = bot        @commands.Cog.listener()    async def on_ready(self):        print(f"Utility Cog has been loaded\n----")        @commands.command(enbaled=False,brief="Get user information.",help="Get the info bout\' a user.",name="userinfo", aliases=["memberinfo", "ui", "mi"])    async def user_info(self, ctx, target: Union[discord.Member, int] = None):    	target = target or ctx.author    	if target.bot == True:	    	    embed = Embed(title="Bot information",colour=self.bot.grey,timestamp=datetime.utcnow())	    	    embed.add_field(name="General:", value=f"**Name:** {str(target)}\n**ID:** {target.id}\n**Nickname:** {target.nick}\n**Bot:** <:greenTick:596576670815879169>", inline=False)	    	    embed.add_field(name="Presence info:", value=f"**Status:** {str(target.status).title()}\n**Activity:** {str(target.activity.type).split('.')[-1].title() if target.activity else 'N/A'} Status: {target.activity.name if target.activity else ''}", inline=False)	    	    embed.add_field(name="Guild info:", value=f"**Created:** " + target.created_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Joined:** " + target.joined_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Top role:** " + target.top_role.mention + "\n" + "**Boosted:** " + "<:redTick:596576672149667840>", inline=False)	    	    embed.set_thumbnail(url=target.avatar_url)	    	    try:	    	        await ctx.send(embed=embed)	    	    except:	    	    	await ctx.send("Hmmm?")    	if target.bot == False and bool(target.premium_since) == True:	    	    embed = Embed(title="User information",colour=self.bot.grey,timestamp=datetime.utcnow())	    	    embed.add_field(name="General:", value=f"**Name:** {str(target)}\n**ID:** {target.id}\n**Nickname:** {target.nick}\n**Bot:** <:redTick:596576672149667840>", inline=False)	    	    embed.add_field(name="Presence info:", value=f"**Status:** {str(target.status).title()}\n**Activity:** {str(target.activity.type).split('.')[-1].title() if target.activity else 'N/A'} Status: {target.activity.name if target.activity else ''}", inline=False)	    	    embed.add_field(name="Guild info:", value=f"**Created:** " + target.created_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Joined:** " + target.joined_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Top role:** " + target.top_role.mention + "\n" + "**Boosted:** " + "<:greenTick:596576670815879169>", inline=False)	    	    embed.set_thumbnail(url=target.avatar_url)	    	    try:	    	        await ctx.send(embed=embed)	    	    except:	    	    	await ctx.send("Hmmm?")    	if target.bot == False and bool(target.premium_since) == False:	    	    embed = Embed(title="User information",colour=self.bot.grey,timestamp=datetime.utcnow())	    	    embed.add_field(name="General:", value=f"**Name:** {str(target)}\n**ID:** {target.id}\n**Nickname:** {target.nick}\n**Bot:** <:redTick:596576672149667840>", inline=False)	    	    embed.add_field(name="Presence info:", value=f"**Status:** {str(target.status).title()}\n**Activity:** {str(target.activity.type).split('.')[-1].title() if target.activity else 'N/A'} Status: {target.activity.name if target.activity else ''}", inline=False)	    	    embed.add_field(name="Guild info:", value=f"**Created:** " + target.created_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Joined:** " + target.joined_at.strftime("%d/%m/%Y %H:%M:%S") + "\n" + "**Top role:** " + target.top_role.mention + "\n" + "**Boosted:** " + "<:redTick:596576672149667840>", inline=False)	    	    embed.set_thumbnail(url=target.avatar_url)	    	    try:	    	        await ctx.send(embed=embed)	    	    except:	    	    	await ctx.send("Hmmm?")        @commands.command(brief="Get the info of the server.",help="Get the info of the server.",name="serverinfo", aliases=["guildinfo", "si", "gi"])    async def server_info(self, ctx):    	  embed = Embed(title="Server information",colour=self.bot.grey,timestamp=datetime.utcnow())    	  embed.set_thumbnail(url=ctx.guild.icon_url)    	  embed.add_field(name="ID", value=ctx.guild.id, inline=False)    	  embed.add_field(name="Owner", value=ctx.guild.owner, inline=False)    	  embed.add_field(name="Region", value=ctx.guild.region, inline=False)    	  embed.add_field(name="Created at", value=ctx.guild.created_at.strftime("%d/%m/%Y %H:%M:%S"), inline=False)    	  embed.add_field(name="Members", value=len(ctx.guild.members), inline=False)    	  embed.add_field(name="Humans", value=len(list(filter(lambda m: not m.bot, ctx.guild.members))), inline=False)    	  embed.add_field(name="Bots", value=len(list(filter(lambda m: m.bot, ctx.guild.members))), inline=False)    	  embed.add_field(name="Text channels", value=len(ctx.guild.text_channels), inline=False)    	  embed.add_field(name="Voice channels", value=len(ctx.guild.voice_channels), inline=False)    	  embed.add_field(name="Categories", value=len(ctx.guild.categories), inline=False)    	  embed.add_field(name="Roles", value=len(ctx.guild.roles), inline=False)    	  await ctx.send(embed=embed)    @commands.command(aliases=['pm'], help="Get the perms of a user.", breif="Get perms of a member, idk.")    @commands.is_owner()    async def perms(self, ctx, member: Union[discord.Member, int] = None):    	if member == None:    		member = ctx.author    	perm_string = ', '.join([str(p[0]).replace("_", " ").title() for p in member.guild_permissions if p[1]])           	embed = discord.Embed(colour=self.bot.grey,title=f"Perms of {member}")     	embed.add_field(name="Guild permissions", value=perm_string, inline=False)    	return await ctx.send(embed=embed)def setup(bot):	bot.add_cog(Utility(bot))