import discordimport datetimefrom typing import Unionimport randomimport inspectimport jsonimport discord.extfrom discord.ext import commandsfrom typing import Unionfrom datetime import datetimefrom discord import Embedimport asyncioclass Utility(commands.Cog):    def __init__(self, bot):    	self.bot = bot        @commands.Cog.listener()    async def on_ready(self):        print(f"Utility Cog has been loaded\n----")        @commands.command(brief="Get user information.",help="Get the info bout\' a user.",name="userinfo", aliases=["memberinfo", "ui", "mi"])    async def user_info(self, ctx, target: Union[discord.Member, int] = None):    	target = target or ctx.author    	    	embed = Embed(title="User information",colour=discord.Colour.from_rgb(64,64,64),timestamp=datetime.utcnow())    	embed.add_field(name="Name", value=str(target), inline=False)    	embed.add_field(name="ID", value=target.id, inline=False)    	embed.add_field(name="Bot?", value=target.bot, inline=False)    	embed.add_field(name="Top role", value=target.top_role.name, inline=False)    	embed.add_field(name="Status", value=str(target.status).title(), inline=False)    	embed.add_field(name="Activity", value=f"{str(target.activity.type).split('.')[-1].title() if target.activity else 'N/A'}: {target.activity.name if target.activity else ''}", inline=False)    	embed.add_field(name="Created at", value=target.created_at.strftime("%d/%m/%Y %H:%M:%S"), inline=False)    	embed.add_field(name="Joined at", value=target.joined_at.strftime("%d/%m/%Y %H:%M:%S"), inline=False)    	embed.add_field(name="Boosted", value=bool(target.premium_since), inline=False)    	    	embed.set_thumbnail(url=target.avatar_url)    	await ctx.send(embed=embed)        @commands.command(brief="Get the info of the server.",help="Get the info of the server.",name="serverinfo", aliases=["guildinfo", "si", "gi"])    async def server_info(self, ctx):    	  embed = Embed(title="Server information",colour=discord.Colour.from_rgb(64,64,64),timestamp=datetime.utcnow())    	  embed.set_thumbnail(url=ctx.guild.icon_url)    	  embed.add_field(name="ID", value=ctx.guild.id, inline=False)    	  embed.add_field(name="Owner", value=ctx.guild.owner, inline=False)    	  embed.add_field(name="Region", value=ctx.guild.region, inline=False)    	  embed.add_field(name="Created at", value=ctx.guild.created_at.strftime("%d/%m/%Y %H:%M:%S"), inline=False)    	  embed.add_field(name="Members", value=len(ctx.guild.members), inline=False)    	  embed.add_field(name="Humans", value=len(list(filter(lambda m: not m.bot, ctx.guild.members))), inline=False)    	  embed.add_field(name="Bots", value=len(list(filter(lambda m: m.bot, ctx.guild.members))), inline=False)    	  embed.add_field(name="Text channels", value=len(ctx.guild.text_channels), inline=False)    	  embed.add_field(name="Voice channels", value=len(ctx.guild.voice_channels), inline=False)    	  embed.add_field(name="Categories", value=len(ctx.guild.categories), inline=False)    	  embed.add_field(name="Roles", value=len(ctx.guild.roles), inline=False)    	  await ctx.send(embed=embed)    @commands.command(aliases=['pm'], help="Get the perms of a user.")    @commands.is_owner()    async def perms(self, ctx, member: Union[discord.Member, int] = None):    	if member == None:    		member = ctx.author    	perm_string = ', '.join([str(p[0]).replace("_", " ").title() for p in member.guild_permissions if p[1]])           	embed = discord.Embed(colour=self.bot.grey,title=f"Perms of {member}")     	embed.add_field(name="Guild permissions", value=perm_string, inline=False)    	return await ctx.send(embed=embed)def setup(bot):	bot.add_cog(Utility(bot))